                INCLUDE rules.asi

; calls to this routine are generated by the compiler to copy
; one "struct" value to another
;
; On entry:
;
;       CX      = Number of bytes to copy


_TEXT           SEGMENT
                ASSUME  CS:_TEXT

                public  N_SCOPY@

N_SCOPY@	proc	near
                push    bp
                mov     bp,sp
                push    si
                push    di
                push    ds
                lds     si,dword ptr 4[bp]
                les     di,dword ptr 8[bp]
                cld
                shr     cx, 1
                rep     movsw
                adc     cx, cx
                rep     movsb
                pop     ds
                pop     di
                pop     si
                pop     bp
                ret     8
N_SCOPY@	endp

_TEXT           ENDS
  
